import './TableStd.css'
import {useState,useEffect} from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';
function TableStd(){
    const [list,setList]=useState([]);


    // get all data from student table using useEffect method  
    useEffect(()=>{
        axios.get('http://localhost:8800/getdata')
       .then((geted)=>{setList(geted.data);
        console.log(geted.data);
        console.log(list);
        })},[])  


const deleteapi=(e)=>{
    const id=e.id;
  if (window.confirm("do you really want to delete the row")){
    axios.post('http://localhost:8800/deletedata',{id:id})
  }
    
}
//local storage setup

const edit=(e)=>{

    const {id, fname,lastname,location,gmail,birthday,education}=e;
    console.log(e)
    localStorage.setItem('id',id)
    localStorage.setItem('fname',fname)
    localStorage.setItem('lname',lastname)
    localStorage.setItem('location',location)
    localStorage.setItem('gmail',gmail)
    localStorage.setItem('birthday',birthday)
    localStorage.setItem('education',education)
}
  

const search=(e)=>{
    console.log(e)
}

    return(
    <div>
        <Link to='./FormStd'>add</Link><br></br>
        <div  className='search'>  <input type='text' placeholder='Search' onClick={(e)=>{search(e.target)}}></input><button>ss</button></div>
 
              
    <table> 
          <thead>
            <th>ID </th>
            <th>FIRSTB NAME</th>
            <th>LAST NAME</th>
            <th>LACATION</th>
            <th>GMAIL</th>
            <th>D.O.B</th>
            <th>EDUCATION</th>
            <th>EDIT</th>
            <th>DELETE</th>
            </thead>
        <tbody>
        {list.map((e,i)=>{
            return(
                <tr key={i}>
                    <td >{e.id}</td>
                    <td>{e.fname}</td>
                    <td>{e.lastname}</td>
                    <td>{e.location}</td>
                    <td>{e.gmail}</td>
                    <td>{e.birthday}</td>
                    <td>{e.education}</td>
                    <td> <Link to='./Edit'><button className='but' onClick={()=>{edit(e)}}>Edit</button> </Link></td>
                    <td><button className='but' onClick={()=>{deleteapi(e)}}>Delete</button></td>
                </tr>
            )

        })}
        
        </tbody>
    </table>


    </div>)

}export default TableStd;
